//import logo from './logo.svg';
//import { initializeApp } from "firebase/app";
import './App.css';
import React, { useState } from "react";
import Button from '@mui/material/Button'
// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { FormControl, InputLabel, Input } from '@mui/material';
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries


function App() {

  const [input, setInput] = useState('');
  const [messages, setMessages] = useState(['Hi']);

  // Your web app's Firebase configuration
  const firebaseConfig = {
    apiKey: "AIzaSyCfUQrKu7eNxE9C8baGIAgMMIl2L4icsew",
    authDomain: "mitai-app.firebaseapp.com",
    projectId: "mitai-app",
    storageBucket: "mitai-app.appspot.com",
    messagingSenderId: "835987929957",
    appId: "1:835987929957:web:44190dabb33940728c3f8c"
  };

  // Initialize Firebase
  const app = initializeApp(firebaseConfig);


  console.log(input);
  console.log(messages);

  const sendMessage = (event) => {
    // all the logic to send message
    event.preventDefault();   //for not refreshing the page for form
    setMessages([...messages, input]);
    setInput('');
  }

  return (
    <div className="App">
      <h1>GHOST Chat</h1>



      {/*input field*/}
      <form>
      <FormControl>
        <InputLabel>Enter message..</InputLabel>
        <Input value={input} onChange={event => setInput(event.target.value)}/>
        <Button variant="contained" color="primary" disabled={!input} type='submit' onClick={sendMessage}>Send Message</Button>
      </FormControl>                
      </form>

      {/*messages*/}
      {
        messages.map(message => (
          <p>{message}</p>
        ))
      }

    </div>
  );
}

export default App;
