//import logo from './logo.svg';
//import { initializeApp } from "firebase/app";
import './App.css';
import React, { useState } from "react";
import Button from '@mui/material'


function App() {

  const [input, setInput] = useState('');
  const [messages, setMessages] = useState(['yoo', 'sup', 'fek']);

  console.log(input);
  console.log(messages);

  const sendMessage = (event) => {
    // all the logic to send message
    event.preventDefault();   //for not refreshing the page for form
    setMessages([...messages, input]);
    setInput('');
  }

  return (
    <div className="App">
      <h1>GHOST Chat</h1>

      {/*input field*/}
      <form>
        <input value={input} onChange={event => setInput(event.target.value)}/>      
        <Button disabled={!input} type='submit' onClick={sendMessage}>Send Message</Button>
      </form>
      {/*messages*/}
      {
        messages.map(message => (
          <p>{message}</p>
        ))
      }

    </div>
  );
}

export default App;
